mapper.update:
UPDATE SHARING
SET PEOPLENUMBER = (
	SELECT COUNT (*) FROM APPLIER WHERE SHAREID = #{shareId}
)
WHERE SHAREID = #{shareId}

mapper.select:

SELECT count(*)
FROM APPLIER
WHERE userId = #{param2} AND shareId=#{param1}


SELECT userId
FROM (SELECT * FROM APPLIER WHERE shareId = #{param1} ORDER BY DBMS_RANDOM.VALUE)
WHERE ROWNUM <= #{param2}
